import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button, Modal, ImageBackground } from 'react-native';
import {css} from '../assets/css/Css';
import Scanner from "../components/Scanner";
import { BarCodeScanner } from 'expo-barcode-scanner';


export default function Home() {
  const [hasPermission, setHasPermission] = useState(null);
  const [scanned, setScanned] = useState(false);
  const [text, setText] = useState('Não escaneado');

  const askFormCameraPermission=() => {
    ( async() => {
      const{status} = await BarCodeScanner.requestPermissionsAsync();
      setHasPermission(status == 'granted')
    })()
  }

  //Permissão
  useEffect(() => {
    askFormCameraPermission();
  }, []);


  const [modalVisible, setModalVisible] = React.useState(false);
  const imgbkg = "../assets/img/fundo.png";

  const [type, setType] = React.useState("");
  const [data, setData] = React.useState("");

  const onCodeScanned = (type,data) => {
      setType(type);
      setData(data);
      setModalVisible(false);
  };

  return (



    <ImageBackground source={require(imgbkg)} style={css.img}>

    <View style={css.container}>
      <Modal
        visible= {modalVisible}
        transparent= {true}
        animationType="fade"
        onRequestClose={() => setModalVisible(false)}
      >

      <View style={css.modal}>
        <Scanner onCodeScanned={onCodeScanned}/>
        <Button title="Cancelar" onPress={() => setModalVisible(false)}/>
      </View>
      </Modal>

    <StatusBar style="auto" />
    <Text>Type: {type}</Text>
    <Text>Data: {data}</Text>



    <Button title="Escanear" onPress={() => setModalVisible(true)}/>
    </View>
      </ImageBackground>
  );
}
