import { StyleSheet} from 'react-native';
import {ImageBackground} from 'react-native';

const imgbkg = "../assets/img/fundo.png";

const css = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#D0ECF6',
    alignItems: 'center',
    justifyContent: 'center',
  },

login_msg:(text='none')=>({
  fontSize: 22,
  color: "#000",
  marginTop: 5,
  marginBottom: 15,
  display: text
}),

login_form:{
  width:"80%",
},

login_input:{
  backgroundColor: "#fff",
  fontSize: 19,
  padding: 7,
  marginBottom: 15
},

login_button:{
  padding: 12,
  backgroundColor:"#D0ECF6",
  alignSelf: "center",
  borderRadius: 5
},

login_logomarca:{
  marginBottom: 10,
},

login_buttonText:{
  fontWeight: "bold",
  fontSize: 22,
  color:"#000"
},



img:{
  flex:1,
  resizeMode: "cover",
  width: "100%",
  justifyContent: "center",
  alignItems: "center",
},

camera:{
  height:"100%",
  width:"100%",
  flex:1,
  flexDirection: "column",
  justifyContent: "flex-end"
},


