},
*/

qr_code:(display='flex')=>({
  width:'100%',
  height:'100%',
  backgroundColor:'#000',
  justifyContent:'center',
  display: display
}),

modal:{
  flex:1,
  justifyContent: "space-around",
  alignItems: "center",
  borderRadius: 8,
  backgroundColor: "#FFF",
  marginVertical:64

}


});
export {css};
