import { StyleSheet} from 'react-native';
import {ImageBackground} from 'react-native';


const css = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#D0ECF6',
    alignItems: 'center',
    justifyContent: 'center',
  },

login_msg:(text='none')=>({
  fontSize: 22,
  color: "#000",
  marginTop: 10,
  marginBottom: 15,
  display: text
}),

login_form:{
  width:"80%",
},

login_input:{
  backgroundColor: "#fff",
  fontSize: 19,
  padding: 7,
  marginBottom: 15
},

login_button:{
  padding: 12,
  backgroundColor:"#A5DEF5",
  alignSelf: "center",
  borderRadius: 5
},

login_buttonText:{
  fontWeight: "bold",
  fontSize: 22,
  color:"#000"
},



img:{
  flex:1,
  resizeMode: "cover",
  width: "100%w",
  justifyContent: "center",

},

