import { StatusBar } from 'expo-status-bar';
import React from 'react';
import {KeyboardAvoidingView, Image, Platform, TouchableOpacity, TextInput, StyleSheet, Text, View, ImageBackground} from 'react-native';
import {css} from '../assets/css/Css'

const imgbkg = "../assets/img/logomarca.png";

export default function Login() {

  return (
    <ImageBackground source={require(imgbkg)} style={css.img}>



    <KeyboardAvoidingView behavior={Platform.OS == "ios" ? "padding" : "height"} style={[css.container]}>
    <View>
      <Image source={require('../assets/img/logomarca.png')}/>
    </View>

    <View>
      <Text style={css.login_msg}> Usuário ou senha inválidos! </Text>
    </View>


    <View style={css.login_form}>
      <TextInput style={css.login_input} placeholder = 'Usuário:'/>
      <TextInput style={css.login_input} placeholder = 'Senha:' secureTextEntry={true}/>
        <TouchableOpacity style={css.login_button}>
          <Text style={css.login_buttonText}> Entrar </Text>
        </TouchableOpacity>
    </View>
    </KeyboardAvoidingView>


    </ImageBackground>
  );
}
